import React from 'react';
import { Input } from './components/login/Input';
import styles from './LoginPage.module.css';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';
import axios from 'axios'; // For making API requests
export const LoginPage = () => {


    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const navigate = useNavigate();

        const handleSignupClick = () => {
            navigate('/sign-up');
        };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('http://localhost:8081/api/login', {
                email,
                password,
            });


            const { token, user } = response.data;

            // Store the token securely (e.g., in local storage or cookies)
            localStorage.setItem('token', token);

            // Update the AuthContext with user data
            login({ token, user });

            // Redirect to the dashboard after successful login
            navigate('/dashboard');

            // You might redirect to a different page or handle authentication state here
        } catch (error) {
            setErrorMessage(error.response?.data?.message || 'Login failed'); // Set error message
        }
    };



    return (
        <div className={styles.container}>
            <div className={styles.content}>
                <h1 className={styles.title}>
                    Macedonian
                    <br />
                    <span className={styles.highlight}>Stock</span> Analysis
                </h1>

                <h2 className={styles.loginTitle}>Login</h2>

                <form onSubmit={handleSubmit} className={styles.form}>
                    <Input
                        label="Email"
                        type="email"
                        placeholder="example@gmail.com"
                    />
                    <Input
                        label="Password"
                        type="password"
                        placeholder="password"
                    />

                    <button
                        type="submit"
                        className={styles.forgotPassword}
                        tabIndex={0}
                    >
                        Forgot password?
                    </button>

                    <button
                        type="submit"
                        className={styles.loginButton}
                    >
                        Login
                    </button>

                    <button
                        type="submit"
                        className={styles.signupLink}
                        tabIndex={0}
                        onClick={handleSignupClick}
                    >
                        Don't have an account? <span className={styles.signupText}>Sign Up</span>
                    </button>
                </form>
            </div>
        </div>
    );
};
