import React from 'react';
import { Input } from './components/login/Input';
import styles from './LoginPage.module.css';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';
import axios from 'axios'; // For making API requests
export const LoginPage = () => {


    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const navigate = useNavigate();

        const handleSignupClick = () => {
            navigate('/sign-up');
        };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setErrorMessage(''); // Clear previous errors

        try {
            const response = await axios.post('http://localhost:8081/api/login', {
                email,
                password,
            });

            // Handle successful login
            alert(response.data); // Example: "Login successful! User: user@example.com"

            navigate('/dashboard'); // Redirect to dashboard
        } catch (error) {
            setErrorMessage(
                error.response?.data || 'Login failed. Please try again.'
            );
        }
    };




    return (
        <div className={styles.container}>
            <div className={styles.content}>
                <h1 className={styles.title}>
                    Macedonian
                    <br />
                    <span className={styles.highlight}>Stock</span> Analysis
                </h1>

                <h2 className={styles.loginTitle}>Login</h2>

                <form onSubmit={handleSubmit} className={styles.form}>
                    <Input
                        label="Email"
                        type="email"
                        placeholder="example@gmail.com"
                        value={email} // Bind to state
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <Input
                        label="Password"
                        type="password"
                        placeholder="password"
                        value={password} // Bind to state
                        onChange={(e) => setPassword(e.target.value)}
                    />

                    <button
                        type="submit"
                        className={styles.forgotPassword}
                        tabIndex={0}
                    >
                        Forgot password?
                    </button>

                    <button
                        type="submit"
                        className={styles.loginButton}
                    >
                        Login
                    </button>

                    <button
                        type="submit"
                        className={styles.signupLink}
                        tabIndex={0}
                        onClick={handleSignupClick}
                    >
                        Don't have an account? <span className={styles.signupText}>Sign Up</span>
                    </button>
                </form>
            </div>
        </div>
    );
};
