import React, { useState } from 'react';
import { InputField } from './components/login/InputField';  // Make sure to adjust your import path for InputField
import styles from './LoginPage.module.css';
import { useNavigate } from 'react-router-dom';
import { login } from '../../service/userService.ts';  // Import the login function from userService
import { UserLogin } from '../../model/Login';  // Import the DTO type

export const LoginPage: React.FC = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');  // For error messages

    const navigate = useNavigate();

    const handleSignupClick = () => {
        navigate('/sign-up');
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();

        const userLoginDTO: Login = {
            email,
            password,
        };

        try {
            // Call the login function from your service
            await login(userLoginDTO);

            // Redirect to the dashboard after a successful login
            navigate('/dashboard');
        } catch (error) {
            setErrorMessage('Login failed. Please check your credentials or try again later.');
            console.error('Login failed:', error);
        }
    };

    return (
        <div className={styles.container}>
            <div className={styles.content}>
                <h1 className={styles.title}>
                    Macedonian
                    <br />
                    <span className={styles.highlight}>Stock</span> Analysis
                </h1>

                <h2 className={styles.loginTitle}>Login</h2>

                {errorMessage && <p className={styles.error}>{errorMessage}</p>} {/* Display error message if any */}

                <form onSubmit={handleSubmit} className={styles.form}>
                    <InputField
                        label="Email"
                        value={email}
                        onChange={setEmail}
                        type="email"
                        required
                        placeholder="example@gmail.com"
                    />
                    <InputField
                        label="Password"
                        value={password}
                        onChange={setPassword}
                        type="password"
                        required
                        placeholder="Enter your password"
                    />

                    <button type="submit" className={styles.loginButton}>
                        Login
                    </button>

                    <button
                        type="button"
                        className={styles.signupLink}
                        onClick={handleSignupClick}
                    >
                        Don't have an account? <span className={styles.signupText}>Sign Up</span>
                    </button>
                </form>
            </div>
        </div>
    );
};
