import React from 'react';
import styles from './NavigationPill.module.css';

export const NavigationPill = ({label, isActive}) => {
    return (
        <a href={link} className={`${styles.pill} ${isActive ? styles.active : styles.default}`}>
            {label}
        </a>
    );
};
