import React, {useEffect} from 'react';
import { useState } from "react";
import GraphComponent from "./components/indicators/GraphComponet";
import Dropdown from "./components/indicators/Dropdown";
import styles from "./components/indicators/Dropdown.module.css";

const Home = () => {
    const [tickers, setTickers] = useState([]);
    const [selectedTicker, setSelectedTicker] = useState("");

    // Fetch tickers from the backend API
    useEffect(() => {
        const fetchTickers = async () => {
            try {
                const response = await fetch("http://localhost:8081/api/tickers");
                const data = await response.json();
                setTickers(data.tickers);
            } catch (error) {
                console.error("Error fetching tickers:", error);
            }
        };

        fetchTickers();
    }, []);


    const handleSelect = (value) => {
        setSelectedTicker(value);
        console.log("Selected:", value);
    };

    return (
        <div className={styles.dashboard}>
            <h1>Dashboard</h1>
            <Dropdown options={tickers} onSelect={handleSelect} />
            {selectedTicker && <p>You selected: {selectedTicker}</p>}
            <GraphComponent />
        </div>
    );
};

export default Home;
